package com.example.myproject;

public class ifElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     
		 int num = 0;

	        if (num > 0) {
	            System.out.println("Positive number");
	        } else if (num < 0) {
	            System.out.println("Negative number");
	        } else {
	            System.out.println("Zero");
	        }
}
}